bitboard generateKingMoves(bitboard& location, bitboard& sameColourPieces) {
    return (kingMoves[location] & (~sameColourPieces));
}

bitboard generateKnightMoves(bitboard& location, bitboard& sameColourPieces) {
    return (kingMoves[location] & (~sameColourPieces));
}

bitboard generatePawnMoves(bitboard& location, int colour, bitboard& sameColourPieces, bitboard& oppositeColourPieces) {
    if (colour == BLACK) {
        return ((blackPawnMovesNormal[location] & (~(sameColourPieces|oppositeColourPieces))) 
                | (blackPawnMovesCaptures[location] & oppositeColourPieces));
    } else if (colour == WHITE) {
        return ((whitePawnMovesNormal[location] & (~(sameColourPieces|oppositeColourPieces))) 
                | (whitePawnMovesCaptures[location] & oppositeColourPieces));
    }
}